exports.getCasInfor = function (serverOptions) {
    switch (serverOptions.hostname) {

        case "lvzhouv3":
            if (serverOptions.cas === "dev") {
                options = {
                    "host": "lvzhouv3.h3c.com",
                    "port": 80,
                    "path": "/v3/base/userauth",
                    "method": "GET"
                };
            } else if (serverOptions.cas === "app") {
                options = {
                    "host": "lvzhouv3.h3c.com",
                    "port": 80,
                    "path": "/v3/app/login",
                    "method": "GET"
                };
            } else if (serverOptions.cas === "web") {
                options = {
                    "host": "lvzhouv3.h3c.com",
                    "port": 80,
                    "path": "/v3/web",
                    "method": "GET"
                };
            }
            break;
        case "localtest":
            if (serverOptions.cas === "dev") {
                options = {
                    "host": "sttest.h3c.com",
                    "port": 80,
                    "path": "/v3/base/userauth",
                    "method": "GET"
                };
            } else if (serverOptions.cas === "app") {
                options = {
                    "host": "sttest.h3c.com",
                    "port": 80,
                    "path": "/v3/app/login",
                    "method": "GET"
                };
            } else if (serverOptions.cas === "web") {
                options = {
                    "host": "sttest.h3c.com",
                    "port": 80,
                    "path": "/v3/web",
                    "method": "GET"
                };
            }
            break;
        case "localhost":
            if (serverOptions.cas === "dev") {
                options = {
                    "host": "test.h3c.com",
                    "port": 80,
                    "path": "/v3/base/userauth",
                    "method": "GET"
                };
            } else if (serverOptions.cas === "app") {
                options = {
                    "host": "test.h3c.com",
                    "port": 80,
                    "path": "/v3/app/login",
                    "method": "GET"
                };
            } else if (serverOptions.cas === "web") {
                options = {
                    "host": "test.h3c.com",
                    "port": 80,
                    "path": "/v3/web",
                    "method": "GET"
                };
            }
            break;
        default:
    }
    return options;
}

